mapper.select:
select 
	member_no as memberNo, member_id as memberId ,member_name as memberName, member_phone as memberPhone, member_addr as memberAddr
 from tbl_member where member_no = #{parameter}

mapper.update:
update tbl_member set member_name = #{memberName},
					member_phone = #{memberPhone},
					member_addr = #{memberAddr}
			where member_no = #{memberNo}
