mapper.update:
update TBL_BOARD
   set board_title = #{boardTitle},
       board_content = #{boardContent},
       IS_SECRET = #{isSecret}
 where board_no = #{boardNo}
   and BOARD_CODE = 'S'

mapper.delete:
delete from TBL_BOARD
where board_no = #{_parameter}
  and BOARD_CODE = 'S'

mapper.update:
UPDATE TBL_COMMENT
   SET COMMENT_CONTENT = #{commentContent}
       WHERE COMMENT_NO = #{commentNo}
